const items = [];

// Step 1: Collect all Post IDs from input1 items (those with 'id')
const processedPosts = new Set(
  $input.all()
    .filter(item => item.json.id)
    .map(item => item.json.Post)
);

// Step 2: Iterate over all items and filter out duplicates
for (const item of $input.all()) {

  // Only process items without 'id' (input2 items)
  if(!item.json.id){

    // Check if the Post ID is already processed
    if(!processedPosts.has(item.json.Post) && item.json.Post!=undefined){
      items.push(item);
    }
  }
}

return items;

