parameters.jsCode:
// Environment variable validation
const requiredVars = [
  'MCP_SERVER_HOST',
  'MCP_SERVER_PORT', 
  'MCP_SERVER_TOKEN',
  'REVIEW_FOLDER_ID',
  'LOG_SPREADSHEET_ID',
  'POSTGRES_HOST',
  'POSTGRES_PORT',
  'POSTGRES_DB',
  'POSTGRES_USER',
  'POSTGRES_PASSWORD'
];

const missingVars = requiredVars.filter(varName => !$env[varName]);

if (missingVars.length > 0) {
  throw new Error(`Missing required environment variables: ${missingVars.join(', ')}`);
}

const requiredVars2 = [
  {name: 'MCP_SERVER_HOST', type: 'string'},
  {name: 'MCP_SERVER_PORT', type: 'number'},
  {name: 'MCP_SERVER_TOKEN', type: 'string'},
  {name: 'REVIEW_FOLDER_ID', type: 'string'},
  {name: 'LOG_SPREADSHEET_ID', type: 'string'},
  {name: 'POSTGRES_HOST', type: 'string'},
  {name: 'POSTGRES_PORT', type: 'number'},
  {name: 'POSTGRES_DB', type: 'string'},
  {name: 'POSTGRES_USER', type: 'string'},
  {name: 'POSTGRES_PASSWORD', type: 'string'}
];

const errors = [];
for (const {name, type} of requiredVars2) {
  if (!$env[name]) {
    errors.push(`Missing ${name}`);
  } else if (type === 'number' && isNaN(Number($env[name]))) {
    errors.push(`${name} must be a number`);
  }
}

if (errors.length > 0) {
  throw new Error(`Environment variable errors: ${errors.join(', ')}`);
}

return [{json: {message: 'Environment variables validated successfully'}}];

// Extract text content from file for classification
const items = $input.all();
const processedItems = [];

for (const item of items) {
  let textContent = '';
  let fileType = '';

  // Get file metadata
  const fileName = item.json.name || 'unknown';
  const mimeType = item.json.mimeType || '';

  // Determine file type for classification context
  if (mimeType.includes('pdf')) {
    fileType = 'PDF Document';
  } else if (mimeType.includes('image')) {
    fileType = 'Image File';
  } else if (mimeType.includes('text') || mimeType.includes('document')) {
    fileType = 'Text Document';
  } else {
    fileType = 'Other File';
  }

  // Extract text content if available
  if (item.binary && item.binary.data) {
    try {
      // For text files, convert binary to text
      if (mimeType.includes('text')) {
        const buffer = Buffer.from(item.binary.data.data, 'base64');
        textContent = buffer.toString('utf8').substring(0, 2000); // Limit text length
      }
    } catch (error) {
      console.log('Error extracting text:', error.message);
    }
  }

  // Prepare classification input
  const classificationInput = {
    fileName: fileName,
    fileType: fileType,
    mimeType: mimeType,
    textContent: textContent || fileName, // Use filename if no text content
    originalData: item.json
  };

  processedItems.push({
    json: classificationInput,
    binary: item.binary
  });
}

return processedItems;

// Build complete folder hierarchy
const files = $input.all();
const allFiles = files.map(item => item.json);

// Separate folders and files
const folders = allFiles.filter(f => f.mimeType === 'application/vnd.google-apps.folder');
const documents = allFiles.filter(f => f.mimeType !== 'application/vnd.google-apps.folder');

// Create folder lookup
const folderLookup = {};
folders.forEach(folder => {
  folderLookup[folder.id] = folder;
});

// Function to get folder path
function getFolderPath(folderId, visited = new Set()) {
  if (visited.has(folderId)) return 'Root'; // Prevent infinite loops
  visited.add(folderId);

  const folder = folderLookup[folderId];
  if (!folder || !folder.parents || folder.parents.length === 0) {
    return 'Root';
  }

  const parentPath = getFolderPath(folder.parents[0], visited);
  return parentPath === 'Root' ? folder.name : `${parentPath}/${folder.name}`;
}

// Build structure analysis
const structure = {
  totalFolders: folders.length,
  totalFiles: documents.length,
  folderHierarchy: {},
  namingPatterns: {},
  fileTypes: {},
  organizationInsights: []
};

// Analyze folder structure
folders.forEach(folder => {
  const path = getFolderPath(folder.id);
  const filesInFolder = documents.filter(doc => 
    doc.parents && doc.parents.includes(folder.id)
  );

  structure.folderHierarchy[path] = {
    id: folder.id,
    name: folder.name,
    fileCount: filesInFolder.length,
    fileTypes: {},
    createdTime: folder.createdTime
  };

  // Analyze file types in this folder
  filesInFolder.forEach(file => {
    const ext = file.name.split('.').pop()?.toLowerCase() || 'no-extension';
    structure.folderHierarchy[path].fileTypes[ext] = 
      (structure.folderHierarchy[path].fileTypes[ext] || 0) + 1;

    structure.fileTypes[ext] = (structure.fileTypes[ext] || 0) + 1;
  });
});

// Analyze naming patterns
const namingPatterns = {};
documents.forEach(file => {
  const name = file.name.toLowerCase();

  // Check for common patterns
  if (name.includes('invoice')) namingPatterns.invoices = (namingPatterns.invoices || 0) + 1;
  if (name.includes('receipt')) namingPatterns.receipts = (namingPatterns.receipts || 0) + 1;
  if (name.includes('contract')) namingPatterns.contracts = (namingPatterns.contracts || 0) + 1;
  if (name.includes('report')) namingPatterns.reports = (namingPatterns.reports || 0) + 1;
  if (/\d{4}-\d{2}-\d{2}/.test(name)) namingPatterns.dateFormatted = (namingPatterns.dateFormatted || 0) + 1;
  if (/\d{2}\/\d{2}\/\d{4}/.test(name)) namingPatterns.dateSlash = (namingPatterns.dateSlash || 0) + 1;
});

structure.namingPatterns = namingPatterns;

// Generate insights
const insights = [];
if (namingPatterns.dateFormatted > 10) {
  insights.push('Uses YYYY-MM-DD date format in filenames');
}
if (Object.keys(structure.fileTypes).includes('pdf') && structure.fileTypes.pdf > 50) {
  insights.push('Heavy PDF document usage detected');
}
if (folders.length > 20) {
  insights.push('Complex folder structure with deep hierarchy');
}

structure.organizationInsights = insights;

return [{ json: structure }];

// Parse AI analysis and create structured rules
const aiAnalysis = $json.message.content;
const structureData = $('Analyze Structure').first().json;

// Create comprehensive analysis result
const result = {
  timestamp: new Date().toISOString(),
  driveStructure: structureData,
  aiAnalysis: aiAnalysis,
  extractedRules: [],
  recommendedActions: [],
  status: 'completed'
};

// Try to parse AI response as JSON if possible
try {
  const parsedAI = JSON.parse(aiAnalysis);
  result.structuredAnalysis = parsedAI;
} catch (e) {
  result.structuredAnalysis = { rawText: aiAnalysis };
}

// Store this analysis for use in other workflows
console.log('Structure analysis completed:', result);

return [{ json: result }];

// Parse extracted rules and prepare for vector storage
const aiResponse = $json.message?.content || $json.content || '';
let extractedRules;

// Enhanced error handling for JSON parsing
try {
  // Clean the response first
  const cleanResponse = aiResponse.replace(/``````\n?/g, '').trim();
  extractedRules = JSON.parse(cleanResponse);

  // Validate structure
  if (!extractedRules.rules || !Array.isArray(extractedRules.rules)) {
    throw new Error('Invalid rules structure');
  }
} catch (e) {
  console.error('JSON parsing failed:', e.message);
  // Create fallback structure
  extractedRules = {
    rules: [{
      id: 'fallback_rule_1',
      name: 'Parsed Content',
      description: aiResponse.substring(0, 500) + '...',
      conditions: { keywords: [], fileTypes: [], contentPatterns: [] },
      actions: { renamePattern: '', targetFolder: '', tags: [] },
      priority: 1,
      category: 'general'
    }]
  };
}

// Process each rule for vector storage with enhanced validation
const processedRules = [];
const timestamp = new Date().toISOString();

if (extractedRules.rules && Array.isArray(extractedRules.rules)) {
  extractedRules.rules.forEach((rule, index) => {
    // Enhanced rule validation and processing
    const processedRule = {
      id: rule.id || `rule_${Date.now()}_${index + 1}`,
      text: `${rule.name || 'Unnamed Rule'}: ${rule.description || 'No description'}`,
      metadata: {
        ruleId: rule.id || `rule_${Date.now()}_${index + 1}`,
        name: rule.name || `Rule ${index + 1}`,
        description: rule.description || '',
        conditions: JSON.stringify(rule.conditions || {}),
        actions: JSON.stringify(rule.actions || {}),
        priority: Number(rule.priority) || 1,
        category: rule.category || 'general',
        keywords: Array.isArray(rule.conditions?.keywords) ? rule.conditions.keywords.join(', ') : '',
        fileTypes: Array.isArray(rule.conditions?.fileTypes) ? rule.conditions.fileTypes.join(', ') : '',
        targetFolder: rule.actions?.targetFolder || '',
        renamePattern: rule.actions?.renamePattern || '',
        tags: Array.isArray(rule.actions?.tags) ? rule.actions.tags.join(', ') : '',
        createdAt: timestamp,
        sourceDocument: $('Download Rules Document').first().json.name || 'Unknown'
      }
    };

    processedRules.push(processedRule);
  });
}

// Return error if no rules processed
if (processedRules.length === 0) {
  throw new Error('No valid rules could be extracted from the document');
}

console.log(`Successfully processed ${processedRules.length} rules`);
return processedRules.map(rule => ({ json: rule }));

// Log successful completion
const totalRules = $input.all().length;
const successfulStores = $input.all().filter(item => item.json.success !== false).length;

return [{
  json: {
    status: 'completed',
    totalRulesProcessed: totalRules,
    successfullyStored: successfulStores,
    timestamp: new Date().toISOString(),
    summary: `Successfully processed and stored ${successfulStores} out of ${totalRules} rules in vector database`
  }
}];

// Enhanced error handling and logging
const errorNode = $json.error?.node || 'Unknown';
const errorMessage = $json.error?.message || $json.message || 'Unknown error';
const errorDetails = $json.error?.description || '';

const errorReport = {
  status: 'error',
  failedAt: errorNode,
  errorMessage: errorMessage,
  errorDetails: errorDetails,
  timestamp: new Date().toISOString(),
  inputData: $json
};

console.error('Workflow Error:', errorReport);

return [{ json: errorReport }];

// Enhanced processing for knowledge base optimization
const aiResponse = $json.message?.content || $json.content || '';
let extractedData;
const timestamp = new Date().toISOString();
const sourceDocument = $('Download Process Document').first().json.name || 'Process Document';

// Parse AI response with enhanced error handling
try {
  const cleanResponse = aiResponse.replace(/``````\n?/g, '').trim();
  extractedData = JSON.parse(cleanResponse);
} catch (e) {
  console.error('JSON parsing failed:', e.message);
  // Create structured fallback
  extractedData = {
    documentInfo: {
      title: sourceDocument,
      version: '1.0',
      lastUpdated: timestamp,
      department: 'Unknown'
    },
    rules: [{
      id: 'fallback_content',
      type: 'general',
      title: 'Document Content',
      description: aiResponse.substring(0, 1000),
      conditions: { keywords: [], fileTypes: [], contentPatterns: [], metadata: [] },
      actions: { namingPattern: '', targetFolder: '', tags: [], additionalActions: [] },
      priority: 1,
      category: 'general',
      examples: [],
      exceptions: []
    }],
    procedures: [],
    glossary: []
  };
}

// Process data for vector storage with optimized chunks
const processedItems = [];
let itemCounter = 1;

// Process document info
if (extractedData.documentInfo) {
  processedItems.push({
    id: `doc_info_${itemCounter++}`,
    text: `Document: ${extractedData.documentInfo.title}. Version: ${extractedData.documentInfo.version || 'Unknown'}. Department: ${extractedData.documentInfo.department || 'Unknown'}. Last Updated: ${extractedData.documentInfo.lastUpdated || 'Unknown'}.`,
    metadata: {
      type: 'document_info',
      title: extractedData.documentInfo.title || sourceDocument,
      version: extractedData.documentInfo.version || '1.0',
      department: extractedData.documentInfo.department || 'Unknown',
      sourceDocument: sourceDocument,
      createdAt: timestamp,
      itemId: `doc_info_${itemCounter - 1}`
    }
  });
}

// Process rules with enhanced metadata
if (extractedData.rules && Array.isArray(extractedData.rules)) {
  extractedData.rules.forEach((rule, index) => {
    const ruleText = `Rule: ${rule.title || `Rule ${index + 1}`}. Description: ${rule.description || 'No description'}. Type: ${rule.type || 'general'}. Category: ${rule.category || 'general'}. Keywords: ${(rule.conditions?.keywords || []).join(', ')}. File Types: ${(rule.conditions?.fileTypes || []).join(', ')}. Naming Pattern: ${rule.actions?.namingPattern || 'No pattern'}. Target Folder: ${rule.actions?.targetFolder || 'No folder'}. Examples: ${(rule.examples || []).join('; ')}. Exceptions: ${(rule.exceptions || []).join('; ')}.`;

    processedItems.push({
      id: rule.id || `rule_${itemCounter++}`,
      text: ruleText,
      metadata: {
        type: 'rule',
        ruleType: rule.type || 'general',
        title: rule.title || `Rule ${index + 1}`,
        description: rule.description || '',
        category: rule.category || 'general',
        priority: Number(rule.priority) || 1,
        keywords: (rule.conditions?.keywords || []).join(', '),
        fileTypes: (rule.conditions?.fileTypes || []).join(', '),
        contentPatterns: (rule.conditions?.contentPatterns || []).join('; '),
        namingPattern: rule.actions?.namingPattern || '',
        targetFolder: rule.actions?.targetFolder || '',
        tags: (rule.actions?.tags || []).join(', '),
        examples: (rule.examples || []).join('; '),
        exceptions: (rule.exceptions || []).join('; '),
        sourceDocument: sourceDocument,
        createdAt: timestamp,
        itemId: rule.id || `rule_${itemCounter - 1}`
      }
    });
  });
}

// Process procedures
if (extractedData.procedures && Array.isArray(extractedData.procedures)) {
  extractedData.procedures.forEach((procedure, index) => {
    const procedureText = `Procedure: ${procedure.name || `Procedure ${index + 1}`}. Steps: ${(procedure.steps || []).join(' -> ')}. Triggers: ${(procedure.triggers || []).join(', ')}. Required Tools: ${(procedure.tools || []).join(', ')}.`;

    processedItems.push({
      id: procedure.id || `procedure_${itemCounter++}`,
      text: procedureText,
      metadata: {
        type: 'procedure',
        name: procedure.name || `Procedure ${index + 1}`,
        steps: (procedure.steps || []).join(' -> '),
        triggers: (procedure.triggers || []).join(', '),
        tools: (procedure.tools || []).join(', '),
        sourceDocument: sourceDocument,
        createdAt: timestamp,
        itemId: procedure.id || `procedure_${itemCounter - 1}`
      }
    });
  });
}

// Process glossary
if (extractedData.glossary && Array.isArray(extractedData.glossary)) {
  extractedData.glossary.forEach((term, index) => {
    const glossaryText = `Term: ${term.term}. Definition: ${term.definition}. Context: ${term.context || 'General usage'}.`;

    processedItems.push({
      id: `glossary_${itemCounter++}`,
      text: glossaryText,
      metadata: {
        type: 'glossary',
        term: term.term,
        definition: term.definition,
        context: term.context || 'General usage',
        sourceDocument: sourceDocument,
        createdAt: timestamp,
        itemId: `glossary_${itemCounter - 1}`
      }
    });
  });
}

if (processedItems.length === 0) {
  throw new Error('No valid content could be extracted from the process document');
}

console.log(`Successfully processed ${processedItems.length} knowledge base items`);
return processedItems.map(item => ({ json: item }));

// Generate comprehensive knowledge base summary
const allItems = $input.all();
const successful = allItems.filter(item => item.json.success !== false);

// Categorize items
const itemsByType = {
  document_info: successful.filter(item => item.json.metadata?.type === 'document_info').length,
  rule: successful.filter(item => item.json.metadata?.type === 'rule').length,
  procedure: successful.filter(item => item.json.metadata?.type === 'procedure').length,
  glossary: successful.filter(item => item.json.metadata?.type === 'glossary').length
};

const summary = {
  status: 'knowledge_base_created',
  totalItems: allItems.length,
  successfullyStored: successful.length,
  failedItems: allItems.length - successful.length,
  breakdown: itemsByType,
  knowledgeBaseIndex: 'document-classification-kb',
  namespace: 'process-rules',
  timestamp: new Date().toISOString(),
  readyForWorkflow3: successful.length > 0,
  summary: `Knowledge base created with ${successful.length} items: ${itemsByType.rule} rules, ${itemsByType.procedure} procedures, ${itemsByType.glossary} glossary terms, and ${itemsByType.document_info} document info entries.`
};

const knowledgeBaseConfig = {
  pineconeIndex: 'document-classification-kb',
  namespace: 'process-rules',
  embeddingModel: 'text-embedding-3-large',
  embeddingDimensions: 1536,
  searchTopK: 10,
  similarityThreshold: 0.7,
  metadataFields: ['type', 'category', 'keywords', 'fileTypes', 'targetFolder', 'namingPattern']
};

return [{
  json: {
    ...summary,
    knowledgeBaseConfig: knowledgeBaseConfig
  }
}];

// Handle validation failure
const errorInfo = {
  status: 'validation_failed',
  message: 'Document content is empty or too short',
  requirement: 'Document must contain at least 50 characters of meaningful content',
  timestamp: new Date().toISOString(),
  suggestion: 'Check if the Google Drive file ID is correct and the document contains text'
};

return [{ json: errorInfo }];

