}
if (!executionId) {
  throw new Error("Input data is missing 'execution_id'. Please ensure it's provided.");
}

// Get the workflow's static data (persists between executions).
const staticData = $getWorkflowStaticData('global');

// --- INITIALIZATION ---
if (!staticData.sessions) {
  staticData.sessions = {};
}
if (!staticData.sessions[workflowId]) {
  staticData.sessions[workflowId] = {};
}

// Prepare output object
const output = {
  session_id: sessionId,
  new: false,
  resume_url: '',
  main_execution: ''
};

// --- CORE LOGIC ---
if (staticData.sessions[workflowId][sessionId]) {
  // Session already exists
  output.new = false;
  output.resume_url = staticData.sessions[workflowId][sessionId].resume_url;
  output.main_execution = staticData.sessions[workflowId][sessionId].execution_id;
} else {
  // New session
  if (!resumeUrl) {
    throw new Error("Input data is missing 'resume_url' for a new session.");
  }

  output.new = true;
  output.resume_url = resumeUrl;
  output.main_execution = executionId;

  // Store session data
  staticData.sessions[workflowId][sessionId] = {
    resume_url: resumeUrl,
    execution_id: executionId,
    first_seen: new Date().toISOString()
  };
}

// --- OUTPUT ---
return output;

// n8n Code Node
// Description: Checks if a session_id already exists in the workflow's static data.
// If it's a new session, it stores the session_id and its resume_url.
// If the session already exists, it returns the previously stored resume_url.

// --- INPUTS ---
// This code expects the following inputs from the previous node:
// $json.session_id: (string) A unique identifier for the session.
// $json.resume_url: (string) The URL associated with the session.

const sessionId = $('A. Entry: Receive Session Info').last().json.session_id;
const resumeUrl = $('A. Entry: Receive Session Info').last().json.resume_url;
const workflowId = $('A. Entry: Receive Session Info').last().json.workflow_id;
const executionId = $('A. Entry: Receive Session Info').last().json.execution_id;

// --- VALIDATION ---
if (!sessionId) {
  throw new Error("Input data is missing 'session_id'. Please ensure it's provided.");
}
if (!workflowId) {
  throw new Error("Input data is missing 'workflow_id'. Please ensure it's provided.");
}
if (!executionId) {
  throw new Error("Input data is missing 'execution_id'. Please ensure it's provided.");
}

// Get the workflow's static data (persists between executions).
const staticData = $getWorkflowStaticData('global');

// --- INITIALIZATION ---
if (!staticData.sessions) {
  staticData.sessions = {};
}
if (!staticData.sessions[workflowId]) {
  staticData.sessions[workflowId] = {};
}

// Prepare output object
const output = {
  session_id: sessionId,
  new: false,
  resume_url: '',
  main_execution: ''
};

// --- CORE LOGIC ---

// Reset session
if (!resumeUrl) {
  throw new Error("Input data is missing 'resume_url' for a new session.");
}

output.new = true;
output.resume_url = resumeUrl;
output.main_execution = executionId;

// Store session data
staticData.sessions[workflowId][sessionId] = {
  resume_url: resumeUrl,
  execution_id: executionId,
  first_seen: new Date().toISOString()
};

// --- OUTPUT ---
return output;

